  if (!room) return { valid: false, message: 'Oda bulunamadƒ±!' };
  if (room.players.length >= 2) return { valid: false, message: 'Oda dolu!' };
  if (room.status !== 'waiting') return { valid: false, message: 'Oyun zaten ba≈ülamƒ±≈ü!' };
  return { valid: true, room };
}

// Odaya katƒ±lma
async function joinRoom(roomCode) {
  const validation = await validateRoomCode(roomCode);
  if (!validation.valid) {
    throw new Error(validation.message);
  }

  const updatedRoom = {
    ...validation.room,
    players: [...validation.room.players, playerData],
    status: 'playing'
  };

  await updateRoomInDatabase(roomCode, updatedRoom);
  return updatedRoom;
}
