// Harf durumu kontrol sistemi
function checkLetterStatus(guess, targetWord) {
  const result = [];
  const targetLetters = targetWord.split('');
  const guessLetters = guess.split('');

  // ƒ∞lk ge√ßi≈ü: Doƒüru pozisyondaki harfler (ye≈üil)
  for (let i = 0; i < 5; i++) {
    if (guessLetters[i] === targetLetters[i]) {
      result[i] = 'correct';
      targetLetters[i] = null; // ƒ∞≈üaretli
      guessLetters[i] = null;  // ƒ∞≈üaretli
    }
  }

  // ƒ∞kinci ge√ßi≈ü: Yanlƒ±≈ü pozisyondaki harfler (sarƒ±)
  for (let i = 0; i < 5; i++) {
    if (guessLetters[i] !== null) {
      const foundIndex = targetLetters.findIndex(letter => letter === guessLetters[i]);
      if (foundIndex !== -1) {
        result[i] = 'present';
        targetLetters[foundIndex] = null;
      } else {
        result[i] = 'absent';
      }
    }
  }

  return result;
}
```

