// G√ºvenli input kontrol√º
function validateInput(input) {
  // Sadece T√ºrk√ße harflere izin ver
  const turkishLetters = /^[A-Z√áƒûIƒ∞√ñ≈û√ú]*$/;

  if (!turkishLetters.test(input.toUpperCase())) {
    throw new Error('Sadece T√ºrk√ße harfler kullanabilirsiniz!');
  }

  if (input.length !== 5) {
    throw new Error('Kelime 5 harfli olmalƒ±dƒ±r!');
  }

  return input.toUpperCase();
}

// Rate limiting
const rateLimitMap = new Map();

function checkRateLimit(playerId) {
  const now = Date.now();
  const playerLimit = rateLimitMap.get(playerId) || { count: 0, resetTime: now };

  if (now > playerLimit.resetTime) {
    // Reset limit
    rateLimitMap.set(playerId, { count: 1, resetTime: now + 60000 }); // 1 dakika
    return true;
  }

  if (playerLimit.count >= 10) { // 10 tahmin/dakika
    return false;
  }

  playerLimit.count++;
  return true;
}
