import * as React from "react"

const MOBILE_BREAKPOINT = 768

export function useIsMobile() {
  const [isMobile, setIsMobile] = React.useState<boolean | undefined>(undefined)

  React.useEffect(() => {
    const mql = window.matchMedia(`(max-width: ${MOBILE_BREAKPOINT - 1}px)`)
    const onChange = () => {
      setIsMobile(window.innerWidth < MOBILE_BREAKPOINT)
    }
    mql.addEventListener("change", onChange)
    setIsMobile(window.innerWidth < MOBILE_BREAKPOINT)
    return () => mql.removeEventListener("change", onChange)
  }, [])

  return !!isMobile
}

client/src/hooks/useFirebase.tsx.content:
import { useEffect, useCallback } from 'react';
import firebase from '../lib/firebase';
import { GameRoom } from '../types/game';

export function useFirebase() {
  const saveRoomToDatabase = useCallback(async (roomCode: string, roomData: GameRoom) => {
    try {
      await firebase.ref(`rooms/${roomCode}`).set(roomData);
    } catch (error) {
      console.error('Error saving room:', error);
      throw new Error('Oda kaydedilemedi');
    }
  }, []);

  const getRoomFromDatabase = useCallback(async (roomCode: string): Promise<GameRoom | null> => {
    try {
      const snapshot = await firebase.ref(`rooms/${roomCode}`).once('value');
      return snapshot.val();
    } catch (error) {
      console.error('Error getting room:', error);
      return null;
    }
  }, []);

  const updateRoomInDatabase = useCallback(async (roomCode: string, updates: Partial<GameRoom>) => {
    try {
      await firebase.ref(`rooms/${roomCode}`).update(updates);
    } catch (error) {
      console.error('Error updating room:', error);
      throw new Error('Oda g√ºncellenemedi');
    }
  }, []);

  const setupRoomListener = useCallback((roomCode: string, callback: (roomData: GameRoom | null) => void) => {
    const roomRef = firebase.ref(`rooms/${roomCode}`);
    const listener = (snapshot: any) => {
      const roomData = snapshot.val();
      callback(roomData);
    };

    roomRef.on('value', listener);

    return () => roomRef.off('value', listener);
  }, []);

  return {
    saveRoomToDatabase,
    getRoomFromDatabase,
    updateRoomInDatabase,
    setupRoomListener
  };
}

client/src/hooks/useGameState.tsx.content:
import { create } from 'zustand';
import { subscribeWithSelector } from 'zustand/middleware';
import { GameState, Player, GameRoom, GameMode } from '../types/game';
import { generateUniqueId, generateRandomAvatar } from '../lib/gameLogic';

interface GameStore extends GameState {
  // Actions
  setPhase: (phase: GameState['phase']) => void;
  setMode: (mode: GameMode) => void;
  setPlayerData: (playerData: Player) => void;
  setRoomCode: (roomCode: string) => void;
  setRoomData: (roomData: GameRoom | null) => void;
  setCurrentInput: (input: string) => void;
  setCurrentRow: (row: number) => void;
  setIsMyTurn: (isMyTurn: boolean) => void;
  setKeyboardStatus: (status: Record<string, any>) => void;
  setError: (error: string | null) => void;
  createPlayer: (name: string) => Player;
  resetGame: () => void;
}

const initialState: GameState = {
  phase: 'menu',
  mode: null,
  playerData: null,
  roomCode: null,
  roomData: null,
  currentInput: '',
  currentRow: 0,
  isMyTurn: false,
  keyboardStatus: {},
  error: null
};

export const useGameState = create<GameStore>()(
  subscribeWithSelector((set, get) => ({
    ...initialState,

    setPhase: (phase) => set({ phase }),
    setMode: (mode) => set({ mode }),
    setPlayerData: (playerData) => set({ playerData }),
    setRoomCode: (roomCode) => set({ roomCode }),
    setRoomData: (roomData) => set({ roomData }),
    setCurrentInput: (currentInput) => set({ currentInput }),
    setCurrentRow: (currentRow) => set({ currentRow }),
    setIsMyTurn: (isMyTurn) => set({ isMyTurn }),
    setKeyboardStatus: (keyboardStatus) => set({ keyboardStatus }),
    setError: (error) => set({ error }),

    createPlayer: (name: string): Player => {
      const player: Player = {
        id: generateUniqueId(),
        name: name || 'Anonim Oyuncu',
        avatar: generateRandomAvatar(),
        status: 'online',
        joinTime: Date.now(),
        lastSeen: Date.now()
      };
      set({ playerData: player });
      return player;
    },

    resetGame: () => set({ ...initialState, playerData: get().playerData })
  }))
);

client/src/hooks/useWordleDuo.tsx.content:
import { useCallback, useEffect } from 'react';
import { useGameState } from './useGameState';
import { useFirebase } from './useFirebase';
import { GameRoom, GameMode, GuessHistory } from '../types/game';
import { 
  generateRoomCode, 
  checkLetterStatus, 
  validateInput, 
  updateKeyboardStatus 
} from '../lib/gameLogic';
import { selectRandomWord, selectDifferentWords, isValidWord } from '../lib/turkishWords';

export function useWordleDuo() {
  const gameState = useGameState();
  const {
    setPhase,
    setMode,
    setRoomCode,
    setRoomData,
    setCurrentInput,
    setCurrentRow,
    setIsMyTurn,
    setKeyboardStatus,
    setError,
    createPlayer
  } = useGameState();

  const {
    saveRoomToDatabase,
    getRoomFromDatabase,
    updateRoomInDatabase,
    setupRoomListener
  } = useFirebase();

  // Create a new room
  const createRoom = useCallback(async (gameMode: GameMode, playerName: string) => {
    try {
      setError(null);
      const player = createPlayer(playerName);
      const roomCode = generateRoomCode();

      let word: string;
      let player1Word: string | undefined;
      let player2Word: string | undefined;

      if (gameMode === 'duel') {
        [player1Word, player2Word] = selectDifferentWords();
        word = player1Word; // Host gets first word
      } else {
        word = selectRandomWord();
      }

      const roomData: GameRoom = {
        code: roomCode,
        mode: gameMode,
        host: player.id,
        players: [player],
        status: 'waiting',
        word,
        player1Word,
        player2Word,
        currentTurn: 0,
        createdAt: Date.now(),
        gameHistory: [],
        totalRows: 1
      };

      await saveRoomToDatabase(roomCode, roomData);

      setMode(gameMode);
      setRoomCode(roomCode);
      setRoomData(roomData);
      setPhase('waiting');

      return roomCode;
    } catch (error) {
      console.error('Error creating room:', error);
      setError('Oda olu≈üturulamadƒ±');
      throw error;
    }
  }, [createPlayer, saveRoomToDatabase, setMode, setRoomCode, setRoomData, setPhase, setError]);

  // Join an existing room
  const joinRoom = useCallback(async (roomCode: string, playerName: string) => {
    try {
      setError(null);
      const room = await getRoomFromDatabase(roomCode);

      if (!room) {
        throw new Error('Oda bulunamadƒ±!');
      }

      if (room.players.length >= 2) {
        throw new Error('Oda dolu!');
      }

      if (room.status !== 'waiting') {
        throw new Error('Oyun zaten ba≈ülamƒ±≈ü!');
      }

      const player = createPlayer(playerName);
      const updatedRoom: GameRoom = {
        ...room,
        players: [...room.players, player],
        status: 'playing'
      };

      // In duel mode, assign second word to joining player
      if (room.mode === 'duel' && room.player2Word) {
        // Joining player gets the second word
      }

      await updateRoomInDatabase(roomCode, updatedRoom);

      setMode(room.mode);
      setRoomCode(roomCode);
      setRoomData(updatedRoom);
      setPhase('playing');

      return updatedRoom;
    } catch (error) {
      console.error('Error joining room:', error);
      setError(error instanceof Error ? error.message : 'Odaya katƒ±lamadƒ±');
      throw error;
    }
  }, [getRoomFromDatabase, createPlayer, updateRoomInDatabase, setMode, setRoomCode, setRoomData, setPhase, setError]);

  // Submit a guess
  const submitGuess = useCallback(async () => {
    if (!gameState.roomCode || !gameState.roomData || !gameState.playerData) {
      return;
    }

    try {
      const input = validateInput(gameState.currentInput);

      if (!isValidWord(input)) {
        throw new Error('Ge√ßerli bir T√ºrk√ße kelime giriniz!');
      }

      const targetWord = gameState.roomData.mode === 'duel' 
        ? (gameState.playerData.id === gameState.roomData.host 
           ? gameState.roomData.player1Word || gameState.roomData.word
           : gameState.roomData.player2Word || gameState.roomData.word)
        : gameState.roomData.word;

      const result = checkLetterStatus(input, targetWord);

      // Update keyboard status
      const newKeyboardStatus = updateKeyboardStatus(
        gameState.keyboardStatus,
        input,
        result
      );
      setKeyboardStatus(newKeyboardStatus);

      // Create guess history entry
      const guessEntry: GuessHistory = {
        rowIndex: gameState.currentRow,
        playerId: gameState.playerData.id,
        playerName: gameState.playerData.name,
        playerAvatar: gameState.playerData.avatar,
        guess: input,
        result,
        timestamp: Date.now()
      };

      // Check if game is won
      const isCorrect = result.every(status => status === 'correct');

      if (isCorrect) {
        // Game won
        const updates: Partial<GameRoom> = {
          gameHistory: [...gameState.roomData.gameHistory, guessEntry],
          status: 'finished'
        };

        await updateRoomInDatabase(gameState.roomCode, updates);
        setPhase('finished');
      } else {
        // Continue game
        const updates: Partial<GameRoom> = {
          gameHistory: [...gameState.roomData.gameHistory, guessEntry],
          currentTurn: gameState.roomData.mode === 'sequential' 
            ? (gameState.roomData.currentTurn + 1) % 2 
            : gameState.roomData.currentTurn,
          totalRows: gameState.roomData.mode === 'sequential' 
            ? gameState.roomData.totalRows + 1 
            : gameState.roomData.totalRows
        };

        await updateRoomInDatabase(gameState.roomCode, updates);

        if (gameState.roomData.mode === 'sequential') {
          setCurrentRow(gameState.currentRow + 1);
        }
      }

      setCurrentInput('');

    } catch (error) {
      console.error('Error submitting guess:', error);
      setError(error instanceof Error ? error.message : 'Tahmin g√∂nderilemedi');
    }
  }, [
    gameState,
    updateRoomInDatabase,
    setKeyboardStatus,
    setPhase,
    setCurrentRow,
    setCurrentInput,
    setError
  ]);

  // Handle keyboard input
  const handleKeyPress = useCallback((key: string) => {
    if (!gameState.roomData || !gameState.playerData) return;

    // Check if it's player's turn (for sequential mode)
    if (gameState.roomData.mode === 'sequential') {
      const isMyTurn = gameState.roomData.players[gameState.roomData.currentTurn]?.id === gameState.playerData.id;
      if (!isMyTurn) return;
    }

    if (key === 'ENTER') {
      if (gameState.currentInput.length === 5) {
        submitGuess();
      }
    } else if (key === 'BACKSPACE') {
      setCurrentInput(gameState.currentInput.slice(0, -1));
    } else if (gameState.currentInput.length < 5 && /^[A-Z√áƒûIƒ∞√ñ≈û√ú]$/.test(key)) {
      setCurrentInput(gameState.currentInput + key);
    }
  }, [gameState, submitGuess, setCurrentInput]);

  // Set up room listener
  useEffect(() => {
    if (!gameState.roomCode) return;

    const unsubscribe = setupRoomListener(gameState.roomCode, (roomData) => {
      if (roomData) {
        setRoomData(roomData);

        // Update turn indicator
        if (roomData.mode === 'sequential' && gameState.playerData) {
          const isMyTurn = roomData.players[roomData.currentTurn]?.id === gameState.playerData.id;
          setIsMyTurn(isMyTurn);
        }

        // Check game phase
        if (roomData.status === 'finished' && gameState.phase !== 'finished') {
          setPhase('finished');
        }
      }
    });

    return unsubscribe;
  }, [gameState.roomCode, gameState.playerData, gameState.phase, setupRoomListener, setRoomData, setIsMyTurn, setPhase]);

  return {
    gameState,
    createRoom,
    joinRoom,
    submitGuess,
    handleKeyPress,
    setError,
    resetGame: () => setPhase('menu')
  };
}

