}

.keyboard-key.present {
  background: linear-gradient(135deg, #f59e0b, #d97706);
  border-color: #d97706;
}

.keyboard-key.absent {
  background: linear-gradient(135deg, #6b7280, #4b5563);
  border-color: #4b5563;
}

/* Glass morphism effects */
.glass-card {
