import React from 'react';
import { motion } from 'framer-motion';
import { LetterStatus } from '../../types/game';

interface TurkishKeyboardProps {
  onKeyPress: (key: string) => void;
  keyboardStatus: Record<string, LetterStatus>;
  disabled?: boolean;
}

export function TurkishKeyboard({ onKeyPress, keyboardStatus, disabled = false }: TurkishKeyboardProps) {
  const keyboard = [
    ['Q', 'W', 'E', 'R', 'T', 'Y', 'U', 'I', 'O', 'P', 'ƒû', '√ú'],
    ['A', 'S', 'D', 'F', 'G', 'H', 'J', 'K', 'L', '≈û', 'ƒ∞'],
    ['Z', 'X', 'C', 'V', 'B', 'N', 'M', '√ñ', '√á']
  ];

  const getKeyStatus = (key: string): LetterStatus => {
    return keyboardStatus[key] || 'empty';
  };

  const containerVariants = {
    hidden: { opacity: 0, y: 20 },
    visible: {
      opacity: 1,
      y: 0,
      transition: {
        duration: 0.3,
        staggerChildren: 0.02
      }
    }
  };

  const keyVariants = {
    hidden: { opacity: 0, scale: 0.8 },
    visible: { opacity: 1, scale: 1 }
  };

  return (
    <motion.div
      variants={containerVariants}
      initial="hidden"
      animate="visible"
      className={`space-y-2 ${disabled ? 'opacity-50 pointer-events-none' : ''}`}
    >
      {keyboard.map((row, rowIndex) => (
        <div key={rowIndex} className="flex justify-center space-x-1">
          {rowIndex === 2 && (
            <motion.button
              variants={keyVariants}
              onClick={() => onKeyPress('ENTER')}
              disabled={disabled}
              className="keyboard-key px-4 py-3 rounded-lg text-white font-bold text-sm glass-button min-w-[80px]"
            >
              ENTER
            </motion.button>
          )}

          {row.map((key) => (
            <motion.button
              key={key}
              variants={keyVariants}
              onClick={() => onKeyPress(key)}
              disabled={disabled}
              className={`
                keyboard-key w-10 h-12 rounded-lg text-white font-bold text-sm
                transition-all duration-200 border border-white/20
                ${getKeyStatus(key) === 'correct' ? 'correct' : ''}
                ${getKeyStatus(key) === 'present' ? 'present' : ''}
                ${getKeyStatus(key) === 'absent' ? 'absent' : ''}
                ${getKeyStatus(key) === 'empty' ? 'glass-button' : ''}
              `}
            >
              {key}
            </motion.button>
          ))}

          {rowIndex === 2 && (
            <motion.button
              variants={keyVariants}
              onClick={() => onKeyPress('BACKSPACE')}
              disabled={disabled}
              className="keyboard-key px-4 py-3 rounded-lg text-white font-bold text-sm glass-button min-w-[80px]"
            >
              ‚å´
            </motion.button>
          )}
        </div>
      ))}

      {disabled && (
        <div className="text-center text-gray-400 text-sm mt-4">
          Sƒ±ranƒ±zƒ± bekleyin...
        </div>
      )}
    </motion.div>
  );
}

client/src/components/WordleDuo/WinScree.content:
import React, { useEffect } from 'react';
import { motion } from 'framer-motion';
import { useWordleDuo } from '../../hooks/useWordleDuo';

export function WinScreen() {
  const { gameState, resetGame } = useWordleDuo();

  // Trigger confetti effect
  useEffect(() => {
    const createConfetti = () => {
      const confettiContainer = document.createElement('div');
      confettiContainer.className = 'fixed inset-0 pointer-events-none z-50';
      document.body.appendChild(confettiContainer);

      for (let i = 0; i < 50; i++) {
        const confetti = document.createElement('div');
        confetti.className = 'absolute w-2 h-2 confetti';
        confetti.style.left = Math.random() * 100 + '%';
        confetti.style.backgroundColor = ['#ef4444', '#f59e0b', '#10b981', '#06b6d4'][Math.floor(Math.random() * 4)];
        confetti.style.animationDelay = Math.random() * 3 + 's';
        confettiContainer.appendChild(confetti);
      }

      setTimeout(() => {
        document.body.removeChild(confettiContainer);
      }, 3000);
    };

    createConfetti();
  }, []);

  if (!gameState.roomData) return null;

  // Determine winner and current player
  const lastGuess = gameState.roomData.gameHistory[gameState.roomData.gameHistory.length - 1];
  const isWinner = lastGuess?.result.every((status: any) => status === 'correct');
  const winner = isWinner ? lastGuess : null;

  // Get current player from localStorage or first available player
  const storedPlayerId = localStorage.getItem('wordle-duo-player-id');
  const currentPlayer = gameState.roomData.players.find(p => p.id === storedPlayerId);
  const isCurrentPlayerWinner = winner && winner.playerId === storedPlayerId;

  return (
    <motion.div
      initial={{ opacity: 0, scale: 0.8 }}
      animate={{ opacity: 1, scale: 1 }}
      transition={{ duration: 0.5 }}
      className="flex-1 flex items-center justify-center p-4"
    >
      <div className="text-center max-w-md">
        <motion.div
          initial={{ opacity: 0, y: 30 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ delay: 0.2 }}
          className="mb-8"
        >
          <div className="text-8xl mb-4">
            {isCurrentPlayerWinner ? 'üéâ' : winner ? 'üòî' : 'ü§ù'}
          </div>
          <h2 className="text-4xl font-bold text-white mb-4">
            {isCurrentPlayerWinner ? 'Tebrikler!' : winner ? 'Kaybettiniz!' : 'Oyun Bitti'}
          </h2>
          {winner && (
            <p className="text-xl text-gray-300 mb-4">
              <span className="text-2xl">{winner.playerAvatar}</span> {winner.playerName} 
              {isCurrentPlayerWinner ? ' (Sen) kazandƒ±n!' : ' kazandƒ±!'}
            </p>
          )}
        </motion.div>

        <motion.div
          initial={{ opacity: 0, y: 20 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ delay: 0.4 }}
          className="glass-card rounded-2xl p-6 mb-8"
        >
          <h3 className="text-xl font-bold text-white mb-4">Oyun √ñzeti</h3>
          <div className="space-y-2 text-sm">
            <div className="flex justify-between text-gray-300">
              <span>Mod:</span>
              <span className="capitalize">{gameState.roomData.mode === 'sequential' ? 'Sƒ±rayla' : 'D√ºello'}</span>
            </div>
            <div className="flex justify-between text-gray-300">
              <span>Toplam Tahmin:</span>
              <span>{gameState.roomData.gameHistory.length}</span>
            </div>
            <div className="flex justify-between text-gray-300">
              <span>Kelime:</span>
              <span className="font-mono uppercase">{gameState.roomData.word}</span>
            </div>
          </div>
        </motion.div>

        <motion.div
          initial={{ opacity: 0, y: 20 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ delay: 0.6 }}
          className="space-y-4"
        >
          <button
            onClick={resetGame}
            className="w-full turkish-red rounded-xl py-4 text-white font-bold text-lg hover:scale-105 transition-transform"
          >
            Yeni Oyun
          </button>

          <button
            onClick={() => {
              if (navigator.share) {
                navigator.share({
                  title: 'Wordle Duo',
                  text: `${winner ? winner.playerName + ' kazandƒ±!' : 'Oyun bitti!'} Wordle Duo'da benimle oyna!`,
                  url: window.location.href
                });
              } else {
                navigator.clipboard.writeText(window.location.href);
                alert('Link kopyalandƒ±!');
              }
            }}
            className="w-full glass-button rounded-xl py-3 text-white font-medium hover:scale-105 transition-transform"
          >
            Sonucu Payla≈ü
          </button>
        </motion.div>
      </div>
    </motion.div>
  );
}

